texts.orig:
server {
	listen       80;
	listen       [::]:80;
	server_name  _;
	root         /usr/share/nginx/html;


	location / {
		proxy_set_header Host $http_host;
		proxy_pass http://unix:/opt/icanhaz/icanhaz.sock;
	}
}

